  }, [counter]) // only runs on initial load cuz of []

  return (
    <div className='App'>
        <button onClick={() => setCounter((prevCount) => prevCount - 1) }>-</button>
        <h1>{ counter }</h1>
        <button onClick={() => setCounter((prevCount) => prevCount + 1) }>+</button>
    </div>
  )
}

export default App;
